sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...
  if (inputs.tskShortDescription == "SASE General Support"){
    outputs.json_output = inputs.SASEGeneralSupportRequestJson;
  }
  else if((inputs.tskShortDescription == "SASE Activation Remote Access")){   
    outputs.json_output = inputs.SASEActivationRemoteAccessRequestJson;
  }
  else if (inputs.tskShortDescription == "Design SASE Configuration"){
    outputs.json_output = inputs.DesignSASEConfigurationRequestJson;
  }
  else if (inputs.tskShortDescription == "SASE Activation SWG") { 
    outputs.json_output = inputs.SASEActivationSWG;
  }
  else if(inputs.tskShortDescription == "SASE Activation Node"){  
    outputs.json_output = inputs.SASEActivationNode;
  }
})(inputs, outputs);

