      serviceOrderNbr = '',
      serviceComponentId ='',
      saseVendor = '',
      serviceLevel = '',
      productType = '',
      oneTimeType = '',
      quantity = '',
      firewall = '',
      sdWan = '',
      saseRemote = '',
      secureWebGateway = '',
