  var customerReqDate = "";

  var jsonVal4;
  var jsonVal5;
  var jsonVal3;


  // Service Address
  var serviceAddress='';//, locationCity,locationStreet,locationZip,locationState,locationCountry;

   var location = new GlideRecord('cmn_location');
  location.query("sys_id", inputs.orderLineItem.location.sys_id);
  location.query();
  if(location.next()){
    serviceAddress = location.street+","+ location.city+","+ location.state+","+ location.country+"-"+location.zip;
  }
  else{
    serviceAddress = "";
  }


  //gs.info("Namrata1 serviceAddress,,,,,,:" +inputs.serviceAddress);
  // gs.info("Namrata Location address:" +inputs.locationAddress);




  var gr2 = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
  gr2.addQuery('order_line_item', inputs.LineItemSys_id);
  gr2.query();
  while (gr2.next()) {
    if(gr2.characteristic == gs.getProperty('Characteristic - SASE Vendor')){
      saseVendor = ""+gr2.characteristic_option_value.toString();
    }
    else if (gr2.characteristic == gs.getProperty('Characteristic - Service Level')){
      serviceLevel = ""+gr2.characteristic_option_value.toString();
