sys_variable_value.value:
(function execute(inputs, outputs) {


  var gdt = new GlideDateTime().getDisplayValue();
  var gdt1 = new GlideDateTime().getNumericValue();
  var nodeId = gs.getProperty('Specification - SASE NODE');

  var serviceID = '',
      serviceOrderNbr = '',
      serviceComponentId ='',
      saseVendor = '',
      serviceLevel = '',
      productType = '',
      oneTimeType = '',
      quantity = '',
      firewall = '',
      sdWan = '',
      saseRemote = '',
      secureWebGateway = '',
      supportInc = '',
      onsiteInstall = '',
      highAvail = '',
      deployType = '',
      onPrem = '',
      size = '',
      vendor = '',
      model = '',
      bandwidthLevel = '',
      piid = '',
      ztnaRemote = '',
      ztna = '';

  var customerCommit = "";
  var customerReqDate = "";

  var jsonVal1;
  var jsonVal2


 // Service Address
  var serviceAddress='';//, locationCity,locationStreet,locationZip,locationState,locationCountry;

   var location = new GlideRecord('cmn_location');
  location.query("sys_id", inputs.orderLineItem.location.sys_id);
  location.query();
  if(location.next()){
    serviceAddress = location.street+","+ location.city+","+ location.state+","+ location.country+"-"+location.zip;
  }
  else{
    serviceAddress = "";
  } 

  var grNode = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
  grNode.addQuery('order_line_item', inputs.LineItemSys_id);
  grNode.addQuery('specification', nodeId);
  grNode.query();
  while (grNode.next()) {
    if(grNode.characteristic == gs.getProperty('Characteristic - SASE Vendor')){
      saseVendor = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Service Level')){
      serviceLevel = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Size')){
      size = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - On Premises CPE Option')){
      onPrem = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Model')){
      model = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Limit Bandwidth in Mbps')){
      bandwidth = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('characteristic.bandwidthLicenselevel')){
      bandwidthLevel = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Firewall Indic.')){
      firewall = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Secure Web Gateway Indic.')){
      secureWebGateway = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Design and Implementation Support Indic.')){
      supportInc = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - On Site Installation Indic.')){
      onsiteInstall = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - High Availability Indic')){
      highAvail = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Deployment Type')){
      deployType = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('characteristics-vendor')){
      vendor = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - ProductInstanceID(PIID)')){
      piid = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('characteristics.productType')){
      productType = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('characteristic.oneTimeEventType')){
      oneTimeType = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('characteristic.ztna')){
