  var serviceAddress='';//, locationCity,locationStreet,locationZip,locationState,locationCountry;

   var location = new GlideRecord('cmn_location');
  location.query("sys_id", inputs.orderLineItem.location.sys_id);
  location.query();
  if(location.next()){
    serviceAddress = location.street+","+ location.city+","+ location.state+","+ location.country+"-"+location.zip;
  }
  else{
    serviceAddress = "";
  }


  //gs.info("Namrata1 serviceAddress,,,,,,:" +inputs.serviceAddress);
  // gs.info("Namrata Location address:" +inputs.locationAddress);




