  location.query();
  if(location.next()){
    serviceAddress = location.street+","+ location.city+","+ location.state+","+ location.country+"-"+location.zip;
  }
  else{
    serviceAddress = "";
  } 

  var grNode = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
