  grNode.addQuery('specification', nodeId);
  grNode.query();
  while (grNode.next()) {
    if(grNode.characteristic == gs.getProperty('Characteristic - SASE Vendor')){
      saseVendor = ""+grNode.characteristic_option_value.toString();
    }
    else if (grNode.characteristic == gs.getProperty('Characteristic - Service Level')){
