sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var nameSpaceNAME;
  var resourceNAME;
  var poID;

  //getting sase profile line item sys_id
  var SASE_PROFILE_sysID = inputs.Order_Line_Item_ID;
  //Query in namespace task to get namespace name
  //Query in namespace task to get namespace name
  var poTask = new GlideRecord('sn_ind_tmt_orm_order_task'); 
  poTask.addEncodedQuery("order_line_item="+SASE_PROFILE_sysID+"^short_descriptionLIKESASE Profile - Create Namespace:^ORshort_descriptionLIKESASE Profile - CreateSASEresource:"); 
  poTask.query();
  while(poTask.next()){
    //Filtering short description to get namespace name
    var shortDescription = ""+poTask.short_description;
    //Passing namespaces name and sase resource in var
    if(shortDescription.includes("SASE Profile - Create Namespace: ") == true){
      nameSpaceNAME = shortDescription.replace("SASE Profile - Create Namespace: ",'');
      outputs.namespaces = nameSpaceNAME;
      poID = poTask.parent;
      outputs.product_order_id = poTask.parent;
    }
    if(shortDescription.includes("SASE Profile - CreateSASEresource: ") == true){
      resourceNAME = shortDescription.replace("SASE Profile - CreateSASEresource: ",'');
      outputs.resource_name = resourceNAME;
    }
  }

  var type = "";
  if(inputs.SrviceTypeName == "Firewall Indic."){
    type = "Firewall";
  }
  if(inputs.SrviceTypeName == "Secure Web Gateway Indic"){
    type = "SWG";
  }
  if(inputs.SrviceTypeName == "ZTNA Remote Access Indic"){
    type = "ZTNA";
  }
  if(inputs.SrviceTypeName == "SD-WAN Indic."){
    type = "SDWAN";
  }

  var answer = "no";
  var tskExists = new GlideRecord("sn_ind_tmt_orm_order_task");
  tskExists.addEncodedQuery("order_line_item="+SASE_PROFILE_sysID+"^parent="+poID+"^short_descriptionLIKECreate SASE "+type+" Service")
  tskExists.query();
  if(tskExists.next()){
    outputs.answer = "yes";
    answer = "yes";
  }

  //SD-WAN Indic, ZTNA Remote Access Indic, Secure Web Gateway Indic, Firewall Indic.
  if(inputs.service_type == "SD-WAN Indic." && answer == "no"){ 
    outputs.resource_path = "apis/v1/namespaces/"+nameSpaceNAME+"/saseservice";
    outputs.service_type = "sdwan"
  }
  if(inputs.service_type == "ZTNA Remote Access Indic" && answer == "no"){
    outputs.resource_path = "apis/v1/namespaces/"+nameSpaceNAME+"/saseservice";
    outputs.service_type = "ztna"
  }
  if(inputs.service_type == "Secure Web Gateway Indic" && answer == "no"){
    outputs.resource_path = "apis/v1/namespaces/"+nameSpaceNAME+"/saseservice";
    outputs.service_type = "SWG"
  }
  if(inputs.service_type == "Firewall Indic." && answer == "no"){
    outputs.resource_path = "apis/v1/namespaces/"+nameSpaceNAME+"/saseservice";
    outputs.service_type = "firewall"
  }
})(inputs, outputs);

