sys_variable_value.value:
(function execute(inputs, outputs) {

  var orderLineID = inputs.orderLineID;
  var accountSysID = inputs.accountSysID;
  var ResponseBody = JSON.parse(inputs.ResponseBody);

  var answer = "no";
  var tskExists = new GlideRecord("sn_ind_tmt_orm_order_task");
  tskExists.addEncodedQuery("order_line_item="+orderLineID+"^parent="+inputs.PO_ID+"^short_descriptionLIKECreate SASE "+inputs.ServiceType+" Service")
  tskExists.query();
  if(tskExists.next()){
    outputs.answer = "yes";
    answer = "yes";
  }

  if(answer == "no"){
    var task = new GlideRecord('sn_ind_tmt_orm_order_task');
    task.initialize();
    task.order_line_item = orderLineID;
    task.account = accountSysID;
    task.parent = inputs.PO_ID;
    task.short_description = "Create SASE "+inputs.ServiceType+" Service: "+inputs.taskName;
    task.work_notes = "HTTP Status Code: "+inputs.HTTP_Status;
    task.work_notes = "Response Body: "+JSON.stringify(ResponseBody);
    task.state = 5;
    task.insert();
  }
  //}

})(inputs, outputs);

