sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var dateTime = new GlideDateTime().getDisplayValue();
  var dt = dateTime.split(' ');
  var f = dt[0].split('-');
  var t = dt[1].split(':');
  var event = new Date(f[0], f[1]-1, f[2], t[0], t[1], t[2]);
  var isoDate = event.toISOString();



  var orXml =  publishOrderXml();
  var xmlString = orXml.toString();
  var xmlClean = global.JSUtil.unescapeText(xmlString);
  outputs.responsexml = xmlClean;
  // gs.info("Ash: "+ xmlClean);



  function publishOrderXml(){

    var strPlayLoad = "";
    var xmlDoc = new XMLDocument2();
    var command = xmlDoc.createElement("command");
    xmlDoc.setCurrentElement(command);

    var messageType = xmlDoc.createElementWithTextValue('messageType','E.Customer.Notification.DEC.OrderMilestone.Command');
    var commandType = xmlDoc.createElementWithTextValue('commandType','Ready To Schedule non-GON');
    var eventTimestamp = xmlDoc.createElementWithTextValue('eventTimestamp',isoDate);//gs.nowDateTime());
    var correlationId = xmlDoc.createElementWithTextValue('correlationId',"889898989");


    var messageSource = xmlDoc.createElementWithTextValue('messageSource','Service Now');
    var entity,name,value; 

    var businessKeys = xmlDoc.createElement('businessKeys');
    for(var i =0; i<3; i++){

      var businessKey= xmlDoc.createElement('businessKey');
      businessKeys.appendChild(businessKey);   

      if(i==0){
        entity = xmlDoc.createElementWithTextValue('entity','Customer');
        name = xmlDoc.createElementWithTextValue('name','Customer Number');
        value = xmlDoc.createElementWithTextValue('value',"");//'SNOW-SwIFT_55000072-0');
        businessKey.appendChild(entity);
        businessKey.appendChild(name);
        businessKey.appendChild(value);
      }
      else if(i==1){
        entity = xmlDoc.createElementWithTextValue('entity','Customer');
        name = xmlDoc.createElementWithTextValue('name','BillingAccountNumber');
        value = xmlDoc.createElementWithTextValue('value',"");//'SNOW-SwIFT_55000072-0');
        businessKey.appendChild(entity);
        businessKey.appendChild(name);
        businessKey.appendChild(value);
      }
      else if(i==2){
        entity = xmlDoc.createElementWithTextValue('entity','Document');
        name = xmlDoc.createElementWithTextValue('name','DocumentID');
        value = xmlDoc.createElementWithTextValue('value', new GlideDateTime().getNumericValue());
        businessKey.appendChild(entity);
        businessKey.appendChild(name);
        businessKey.appendChild(value);
      }        
    }	
    var payload = xmlDoc.createElementWithTextValue('payload',strPlayLoad);
    return xmlDoc;  
  }


})(inputs, outputs);

