sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

    // ... code ...

  var orderLineId = inputs.orderLineId;
  var created =  ""+inputs.created;

  var gt = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); gt.addEncodedQuery("order_line_item="+orderLineId+"^characteristic=cb7acbe187d749d025a20edc0ebb3506"); 
  gt.query();
  if(gt.next()){
    gt.characteristic_option_value = (new Date(new GlideDateTime(created).getNumericValue()).toISOString());
    gt.update();

  }
})(inputs, outputs);

