sys_variable_value.value:
(function execute(inputs, outputs) {


  var accountSysID = inputs.accountSysID;
  var product_specification = inputs.product_specification;
  var orderID = inputs.orderID;
  var soID = "";
  var orderLineID = "";

  //Getting service order ID
  var gt = new GlideRecord('sn_ind_tmt_orm_order_line_item'); 
  gt.addEncodedQuery("order="+orderID+"^product_specification=806f285287f6095025a20edc0ebb35d6"); 
  gt.query();
  if(gt.next()){
    var sysID = gt.sys_id;
    orderLineID = ""+gt.sys_id;
    var so = new GlideRecord("sn_ind_tmt_orm_service_order");
    so.addQuery('order_line_item', sysID);
    so.addQuery('short_description','Service Order for CPE');
    so.query();
    if(so.next()){
      soID = ""+so.sys_id;
    }
  }

  if(product_specification == "806f285287f6095025a20edc0ebb35d6"){//SASE CPE ID
    var gr = new GlideRecord('sn_ind_tmt_orm_order_line_item'); 
    gr.addEncodedQuery("order="+orderID+"^product_specification=288e6c1287f6095025a20edc0ebb35dd"); 
    gr.query();
    if(gr.next()){
      //getting sase profile line item sys_id
      var SASE_PROFILE_sysID = gr.sys_id;
      //Checking Node TDG Complete Task Completed or Not
      var poTask = new GlideRecord('sn_ind_tmt_orm_order_task'); 
      poTask.addEncodedQuery("order_line_item="+SASE_PROFILE_sysID+"^short_description=Node TDG Complete Task"); 
      poTask.query();
      if(poTask.next()){
        if(poTask.state == 5){
          var task = new GlideRecord('sn_ind_tmt_orm_order_task');
          task.initialize();
          task.order_line_item = orderLineID;
          task.account = accountSysID;
          task.parent = soID;
          task.short_description = "Trigger CPE Fulfilment";
          task.state = 5;
          task.insert();
        }
      }
    }
  }
  if(product_specification == "288e6c1287f6095025a20edc0ebb35dd"){//SASE Node ID
    //Checking Create SASE Claim License Task Completed or Not
    var gr1 = new GlideRecord('sn_ind_tmt_orm_order_line_item'); 
    gr1.addEncodedQuery("order="+orderID+"^product_specification=806f285287f6095025a20edc0ebb35d6"); 
    gr1.query();
    if(gr1.next()){
      //getting sase profile line item sys_id
      var SASE_PROFILE_sysID1 = gr1.sys_id;
      //Checking Node TDG Complete Task Completed or Not
      var poTask = new GlideRecord('sn_ind_tmt_orm_order_task'); 
      poTask.addEncodedQuery("order_line_item="+SASE_PROFILE_sysID1+"^short_descriptionLIKESASE Claim License"); 
      poTask.query();
      if(poTask.next()){
        //Checking Node TDG Complete Task Completed or Not
        if(poTask.state == 5){
          var task = new GlideRecord('sn_ind_tmt_orm_order_task');
          task.initialize();
          task.order_line_item = orderLineID;
          task.account = accountSysID;
          task.parent = soID;
          task.short_description = "Trigger CPE Fulfilment";
          task.state = 5;
          task.insert();
        }
      }
    }
  }
})(inputs, outputs);

