sys_variable_value.value:
(function execute(inputs, outputs) {
var request, response, status, ordlId;
		ordlId = 'ORDL0005964';
		request = new sn_ws.RESTMessageV2();
		request.setEndpoint('https://lumendev2.service-now.com/api/sn_ind_tmt_orm/service_order_characteristics_ci/ORDL0005964');
		request.setHttpMethod('GET');
		request.setRequestHeader("Accept","application/json");
		request.setRequestHeader('Content-Type','application/json');
		response = request.execute();
		gs.info('Response body' + response.getBody());
		var JSONData = new global.JSON().decode(response.getBody());
  		outputs.outputresponse = "Response  " + JSON.stringify(response.getBody());
})(inputs, outputs);

