sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...
  var task = new GlideRecord('sn_ind_tmt_orm_order_task');
  task.initialize();
  task.order_line_item = inputs.orderLineID;
  task.account = inputs.accountID;
  task.parent = inputs.parentID;
  task.short_description = "Node TDG Complete Task";
  if(inputs.orderLineAction == "add" || inputs.serviceLevelUpgrade == "true" || inputs.supportUpgrade == "true" || inputs.serviceUpgradeFirewall == "true" || inputs.serviceUpgradeSWG == "true"){
    task.state = 1;
  }else{
    task.state = 2;
  }
  task.insert();
})(inputs, outputs);

