sys_variable_value.value:
(function execute(inputs, outputs) {


    var gdt = new GlideDateTime().getDisplayValue();
    var gdt1 = new GlideDateTime().getNumericValue();

    var piid = '';
    var jsonVal1;
    var jsonVal2;

    var gr = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
    gr.addQuery('order_line_item', inputs.LineItemSys_id);
    gr.query();
    while (gr.next()) {
        if (gr.characteristic == gs.getProperty('Characteristic - ProductInstanceID(PIID)')) {
            piid = "" + gr.characteristic_option_value.toString();
        }

    }


    if (inputs.taskState == "closed") {
        jsonVal2 = {
            "messageId": "SN-" + inputs.orderId,
            "messageTime": gdt,
            "messageType": "serviceNowCreateTask",
            "messageName": "ODC.TEST2.WORKMATE.ENTERPRISE.WORK.SERVICENOWTASK.COMMAND",
            "responseName": "ODC.TEST2.LUMEN-SERVICENOW.ENTERPRISE.ORDERORCHESTRATION.SERVICENOW.WORKMATETASKRESPONSE.COMMAND",
            "pattern": "Command",
            "version": inputs.version,
            "source": "LUMEN-SERVICENOW",
            "uniqueId": inputs.orderId,
            "correlationId": gdt1,
            "payload": {
                "key": "LUMEN-SERVICENOW_" + inputs.TaskNumber,
                "workmatePayload": {
                    "sourceTaskId": inputs.TaskNumber,
                    "sourceSystemName": "LUMEN-SERVICENOW",
                    "taskName": inputs.tskShortDescription,
                    "createdById": "LUMEN-SERVICENOW",
                    "notes": "Test Notes",
                    "commonInformation": {
                        "accountNumber": inputs.accountNumber,
                        "customerId": inputs.customerId,
                        "customerName": inputs.customerName,
                        "orderSource": "LUMEN-SERVICENOW",
                        "orchestratorSystem": "LUMEN-SERVICENOW",
                        "productOfferName": inputs.productName,
                        "productName": inputs.productName,
                        "serviceComponentId": piid,
                        "serviceId": piid,
                        "orderId": inputs.orderNumber,
                        "orderVersion": inputs.orderVersion
                    },
                    "statusDetails": {
                        "statusCode": "800",
                        "status": "Completed"
                    },
                    "taskInstParamRequestList": [

                        {
                            "name": "OrderSNOWSysID",
                            "value": "" + inputs.orderId
                        },
                        {
                            "name": "serviceOrderNbr",
                            "value": inputs.orderNumber + "/" + piid
                        }
                    ]
                }
            }

        }

        //gs.info(">>MEDR Technical Data Gathering: " + JSON.stringify(jsonVal1));
        //outputs.responsestr = JSON.stringify(jsonVal1);
        outputs.prodorderobj = JSON.stringify(jsonVal2);
    } else {
        jsonVal1 = {
            "messageId": "SN-" + inputs.orderId,
            "messageTime": gdt,
            "messageType": "serviceNowCreateTask",
            "messageName": "ODC.TEST2.WORKMATE.ENTERPRISE.WORK.SERVICENOWTASK.COMMAND",
            "responseName": "ODC.TEST2.LUMEN-SERVICENOW.ENTERPRISE.ORDERORCHESTRATION.SERVICENOW.WORKMATETASKRESPONSE.COMMAND",
            "pattern": "Command",
            "version": inputs.version,
            "source": "LUMEN-SERVICENOW",
            "uniqueId": inputs.orderId,
            "correlationId": gdt1,
            "payload": {
                "key": "LUMEN-SERVICENOW_" + inputs.TaskNumber,
                "workmatePayload": {
                    "sourceTaskId": inputs.TaskNumber,
                    "sourceSystemName": "LUMEN-SERVICENOW",
                    "taskName": inputs.tskShortDescription,
                    "createdById": "LUMEN-SERVICENOW",
                    "notes": "Test Notes",
                    "commonInformation": {
                        "accountNumber": inputs.accountNumber,
                        "customerId": inputs.customerId,
                        "customerName": inputs.customerName,
                        "orderSource": "LUMEN-SERVICENOW",
                        "orchestratorSystem": "LUMEN-SERVICENOW",
                        "productOfferName": inputs.productName,
                        "productName": inputs.productName,
                        "serviceComponentId": piid,
                        "serviceId": piid,
                        "orderId": inputs.orderNumber,
                        "orderVersion": inputs.orderVersion
                    },
                    "taskInstParamRequestList": [

                        {
                            "name": "OrderSNOWSysID",
                            "value": "" + inputs.orderId
                        },
                        {
                            "name": "serviceOrderNbr",
                            "value": inputs.orderNumber + "/" + piid
                        }
                    ]
                }
            }

        }
        outputs.prodorderobj = JSON.stringify(jsonVal1);
    }


})(inputs, outputs);

