sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var orderLineId = inputs.oli_sys_id;
  var created =  inputs.created.toString();
  var updatedDate =  inputs.updatedDate.toString();

  var gt = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); gt.addEncodedQuery("order_line_item="+orderLineId+"^characteristic=876a4b6587d749d025a20edc0ebb3523^ORcharacteristic=db5ac76587d749d025a20edc0ebb3561^ORcharacteristic=f43a8fa187d749d025a20edc0ebb354c^ORcharacteristic=024ac76587d749d025a20edc0ebb355f^ORcharacteristic=320b07a587d749d025a20edc0ebb35f9^ORcharacteristic=686bcf6587d749d025a20edc0ebb3575^ORcharacteristic=334b83a587d749d025a20edc0ebb35d2"); 
  gt.query();
  while(gt.next()){
    gt.characteristic_option_value = (new Date(new GlideDateTime(created).getNumericValue()).toISOString());
    //created + " GMT";



    gt.update();
  }
  //Update install date
 // var gt1 = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); gt1.addEncodedQuery("order_line_item="+orderLineId+"^characteristic=138ac36587d749d025a20edc0ebb35a8"); 
  //gt1.query();
 // if(gt1.next()){
   // gt1.characteristic_option_value = updatedDate;
   // gt1.update();
  //}


  //Updating contract term end date

  // Add your code here
  var orderLineStartDate = [];
  var term = [];

  gs.info("Checking Term: "+term);
  gs.info("Checking order_line_item: "+orderLineId);

  var startDate = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
  startDate.addEncodedQuery("order_line_item="+orderLineId+"^characteristic=cf5d05d4873e095084b2a18e0ebb35db^ORcharacteristic=ab3bcf6587d749d025a20edc0ebb356b");
  startDate.query();
  while(startDate.next()) {
    if (startDate.characteristic == "cf5d05d4873e095084b2a18e0ebb35db") { //term
      term.push('' + startDate.characteristic_option_value); //36
    }
    if (startDate.characteristic == "ab3bcf6587d749d025a20edc0ebb356b") {
      orderLineStartDate.push('' + startDate.characteristic_option_value);
    }
  }
  gs.info("Checking orderLineStartDate: "+orderLineStartDate);


  //Updating end date in chars.
  var gdt = new GlideDateTime(orderLineStartDate);
  var ed = new GlideDateTime(gdt);
  ed.addMonthsUTC(term);
  var abc = ed;
  gs.info("Checking abc: "+abc);
  var updateEndDate = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
 updateEndDate.addEncodedQuery("order_line_item="+orderLineId+"^characteristic=9b2b0ba587d749d025a20edc0ebb355f");
  updateEndDate.query();
  if (updateEndDate.next()) {
    updateEndDate.characteristic_option_value = 
      (new Date(new GlideDateTime(abc).getNumericValue()).toISOString());

      //abc + " GMT"; 
    //gDate;// gDate; // + " 00:00:00";
    updateEndDate.update();
  }

})(inputs, outputs);

