sys_variable_value.value:
(function execute(inputs, outputs) {

  var orderLineId = inputs.oli_sys_id;
  var created =  inputs.created.toString();
  var updatedDate =  inputs.updatedDate.toString();

  var gt = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); gt.addEncodedQuery("order_line_item="+orderLineId+"^characteristic="+gs.getProperty("sn_ind_tmt_orm.customer.order.char.customer_sign_date")+"^ORcharacteristic="+gs.getProperty("Characteristic - Customer Requested Date")+"^ORcharacteristic="+gs.getProperty("sn_ind_tmt_orm.Char - Customer Acceptance Date")+"^ORcharacteristic="+gs.getProperty("Characteristic - Customer Commit Date")+"^ORcharacteristic="+gs.getProperty("sn_ind_tmt_orm.Char - Customer Commit Set Date")+"^ORcharacteristic="+gs.getProperty("sn_ind_tmt_orm.Char - Customer Revised Commit Set Date")+"^ORcharacteristic="+gs.getProperty("sn_ind_tmt_orm.Char - Customer Revised Commit Date")); 
  gt.query();
  while(gt.next()){
    gt.characteristic_option_value = (new Date(new GlideDateTime(created).getNumericValue()).toISOString());
    gt.update();
  }

  //Updating contract term end date

  var orderLineStartDate = "";
  var term = "";
  var startDate = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
  startDate.addEncodedQuery("order_line_item="+orderLineId+"^specification="+gs.getProperty("productSpecification.SASE CPE")+"^characteristic="+gs.getProperty("Characteristic - Term")+"^ORcharacteristic="+gs.getProperty("Characteristic - Contract Term Start Date"));
  startDate.query();
  while(startDate.next()) {
    if (startDate.characteristic == gs.getProperty("Characteristic - Term")) { //term
      term = ""+startDate.characteristic_option_value; //36
    }
    if (startDate.characteristic == gs.getProperty("Characteristic - Contract Term Start Date")) {
      orderLineStartDate = ""+startDate.characteristic_option_value;
    }
  }
  //gs.info("Checking orderLineStartDate: "+orderLineStartDate);


  //Updating end date in chars.
  var gdt = new GlideDateTime(orderLineStartDate);
  var ed = new GlideDateTime(gdt);
  ed.addMonthsUTC(term);
  var abc = ed;
  var updateEndDate = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
  updateEndDate.addEncodedQuery("order_line_item="+orderLineId+"^characteristic="+gs.getProperty("sn_ind_tmt_orm.Characteristic-Contract Term End Date"));
  updateEndDate.query();
  if (updateEndDate.next()) {
    updateEndDate.characteristic_option_value = 
      (new Date(new GlideDateTime(abc).getNumericValue()).toISOString());
    updateEndDate.update();
  }

})(inputs, outputs);

