sys_variable_value.value:
(function execute(inputs, outputs) {

  if(inputs.chkEdgeCreated == "no" && inputs.chkNameSpaceResourceServicesTask == "yes"){

    var edgeName = JSON.parse(inputs.edgeResponseBody);
    var accountSysID = inputs.account;
    var orderLineID = inputs.current_order_line_sys_id;

    var so = new GlideRecord('sn_ind_tmt_orm_service_order');
    so.addEncodedQuery("order_line_item="+inputs.current_order_line_sys_id+"^short_description=Service Order for CPE");
    so.query();
    if(so.next()){
      var chkEdgeTask = new GlideRecord('sn_ind_tmt_orm_order_task'); 
      chkEdgeTask.addEncodedQuery("order_line_item="+orderLineID+"^short_descriptionSTARTSWITHEdge Created:"); 
      chkEdgeTask.query();
      var ckhCount = chkEdgeTask.getRowCount();
      if(ckhCount == 0){
        var task = new GlideRecord('sn_ind_tmt_orm_order_task');
        task.initialize();
        task.parent = so.sys_id;
        task.order_line_item = orderLineID;
        task.account = accountSysID;
        task.short_description = "Edge Created: "+ inputs.edgeID1;
        task.work_notes = "HTTP Status Code: "+inputs.HTTP_StatusEdgeCode;
        task.work_notes = "Response Body: "+JSON.stringify(edgeName)
        if(inputs.HTTP_StatusEdgeCode == 200){
          task.state = 5;
        }else{
          task.state = 7;
        }
        task.insert();
      }
    }
    //UPDATING DEVICE CHAR WITH EDGE NAME
    var deviceName = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); 
    deviceName.addEncodedQuery("order_line_item="+inputs.current_order_line_sys_id+"^characteristic.name=Device Name"); 
    deviceName.query(); 
    if(deviceName.next()){
      deviceName.characteristic_option_value = inputs.edgeID1;
      deviceName.update();
    }
    //}
  }
})(inputs, outputs);

