sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var orderID = inputs.orderID;
  var timeStamp = new GlideDateTime();
  var timeInSeconds = timeStamp.getNumericValue();
  var piid = "";
  var namespaces = "";
  var modelValue = "";

  //Getting Tokens and Url
  outputs.url = gs.getProperty("vendorAPI_URL");
  outputs.authorization_1 = "Bearer "+gs.getProperty("vendorAPI_AccessToken1");
  outputs.authorization_2 = "Bearer "+gs.getProperty("vendorAPI_AccessToken2");

  var gr = new GlideRecord('sn_ind_tmt_orm_order_line_item'); 
  gr.addEncodedQuery("order="+orderID+"^product_specification="+gs.getProperty("productSpecification.SASE Profile")+"^ORproduct_specification="+gs.getProperty("productSpecification.SASE CPE")); 
  gr.query();
  while(gr.next()){
    //GETTING SASE PROFILE LINE ITEM DETAILS
    if(gr.product_specification == gs.getProperty("productSpecification.SASE Profile")){
      var SASE_PROFILE_sysID = gr.sys_id;
      //GETTING SASE NAMESPACE AND SASE RESOURCE
      var poTask = new GlideRecord('sn_ind_tmt_orm_order_task'); 
      poTask.addEncodedQuery("order_line_item="+SASE_PROFILE_sysID+"^short_descriptionLIKESASE Profile - Create Namespace:^ORshort_descriptionLIKESASE Profile - CreateSASEresource:"); 
      poTask.query();
      while(poTask.next()){
        //GETTING SASE NAMESPACE AND SASE RESOURCE
        if(poTask.short_description.includes("SASE Profile - Create Namespace:") == true){
          namespaces = ""+poTask.short_description.replace("SASE Profile - Create Namespace: ",'')
          outputs.namespaces = poTask.short_description.replace("SASE Profile - Create Namespace: ",'')
        }
        if(poTask.short_description.includes("SASE Profile - CreateSASEresource: ") == true){
          outputs.saseresource = poTask.short_description.replace("SASE Profile - CreateSASEresource: ",'')
        }
      }
    }
  }

  //GETTING SASE CPE LINE ITEM DETAILS
  var SASE_CPE_sysID = inputs.current_order_line_sys_id;
  //Getting Product Order ID
  var CPE_PO_ID = "";
  var CPEpoTask = new GlideRecord('sn_ind_tmt_orm_product_order'); 
  CPEpoTask.addEncodedQuery("order_line_item="+SASE_CPE_sysID); 
  CPEpoTask.query();
  if(CPEpoTask.next()){
    CPE_PO_ID = ""+CPEpoTask.sys_id
  }

  var term = [];
  //Getting PIID and SASE vendor.
  var characteristics = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); 
  characteristics.addEncodedQuery("order_line_item="+SASE_CPE_sysID+"^domain_order="+CPE_PO_ID+"^characteristic="+gs.getProperty("Characteristic - ProductInstanceID(PIID)")+"^ORcharacteristic="+gs.getProperty("Characteristic - Role")+"^ORcharacteristic="+gs.getProperty("Characteristic - SASE Vendor")+"^ORcharacteristic="+gs.getProperty("Characteristic - High Availability Indic")+"^ORcharacteristic="+gs.getProperty("Characteristic - Model")+"^ORcharacteristic="+gs.getProperty("Characteristic - Limit Bandwidth in Mbps")+"^ORcharacteristic="+gs.getProperty("Characteristic - Term"));
  characteristics.query();
  while(characteristics.next()){
    if(characteristics.characteristic.name == "ProductInstanceId"){
      piid = ""+characteristics.characteristic_option_value;
      outputs.sase_cpe_piid = ""+characteristics.characteristic_option_value;
    }
    if(characteristics.characteristic.name == "SASE Vendor"){
      outputs.sase_cpe_vendor = ""+characteristics.characteristic_option_value;
    }
    if(characteristics.characteristic.name == "Role"){
      outputs.has_role = ""+characteristics.characteristic_option_value;
    }
    if(characteristics.characteristic.name == "High Availability Indic"){
      var checkIndi = "";
      checkIndi = ""+characteristics.characteristic_option_value;
      if(checkIndi == "Yes" || checkIndi == "yes"){
        outputs.high_availability_indic = "true";
      }
      if(checkIndi == "No" || checkIndi == "no"){
        outputs.high_availability_indic = "false";
      }
    }
    if(characteristics.characteristic.name == "Model"){
      modelValue = ""+characteristics.characteristic_option_value;
      outputs.model = ""+characteristics.characteristic_option_value;
    }
    if(characteristics.characteristic.name == "Limit Bandwidth in Mbps"){
      outputs.limit_bandwidth_in_mbps = ""+characteristics.characteristic_option_value;
    }
    if(characteristics.characteristic.name == "Term"){
      term.push(""+characteristics.characteristic_option_value)
      outputs.term = term[0];    
    }
  }


  //Making Edge Name - CLLI+PIID
  var totalChars = inputs.clli+piid+"-"+modelValue;
  //if(totalChars.length > 20){
  //totalChars = totalChars.substring(0,20);
  //}
  outputs.sase_edge_id = totalChars; 


  var loc = new GlideRecord('cmn_location');
  loc.addQuery('sys_id', inputs.locationID);
  loc.query();
  if (loc.next()) {
    outputs.address_ = ""+loc.street;
    outputs.address2 = ""+loc.u_address_2;
    outputs.city = ""+loc.city;
    outputs.state = ""+loc.state;
    outputs.country = ""+loc.country;
    outputs.postalcode = ""+loc.zip;
    outputs.lat = ""+loc.latitude;
    outputs.longitude = ""+loc.longitude;
    outputs.glmid = ""+loc.u_glm_id;
  }


  //Getting Contact Details
  var contacts = [];
  var con = new GlideRecord('sn_ind_tmt_orm_order_line_item_contact');
  con.addEncodedQuery("order_line_item="+inputs.current_order_line_sys_id);
  con.query();
  while(con.next()) {
    var data = {}
    data.firstName = ""+con.first_name;
    data.lastName = ""+con.last_name;
    data.email= ""+con.email;
    data.role= ""+con.u_role;
    contacts.push(data)
  }
  outputs.contacts = JSON.stringify(contacts);



  //Checking EDGE Task and NameSpace, Resource, Services Tasks Created Checks
  var chkEdgeTask = "no";
  var tskExists1 = new GlideRecord("sn_ind_tmt_orm_order_task");
  tskExists1.addEncodedQuery("order_line_item="+inputs.current_order_line_sys_id+"^short_descriptionLIKEEdge Created:")
  tskExists1.query();
  if(tskExists1.next()){
    chkEdgeTask = "yes";
  }
  var chkNameSpaceResourceServicesTask = "no";
  var tskExists2 = new GlideRecord("sn_ind_tmt_orm_order_task");
  tskExists2.addEncodedQuery("order_line_item="+inputs.current_order_line_sys_id+"^short_descriptionLIKENameSpace, Resource, Services Tasks Created")
  tskExists2.query();
  if(tskExists2.next()){
    chkNameSpaceResourceServicesTask = "yes";
  }

  outputs.check_edge_task = chkEdgeTask;
  outputs.namespace_resource_services_tasks = chkNameSpaceResourceServicesTask;

  if(chkEdgeTask == "no" && chkNameSpaceResourceServicesTask == "yes"){
    outputs.edge_resource_path = "apis/v1/namespaces/"+namespaces+"/edge";
  }

})(inputs, outputs);

