sys_variable_value.value:
(function execute(inputs, outputs) {


  var gdt = new GlideDateTime().getNumericValue();
  var gdt1 = new Date(gdt);
  var instance_current = gs.getProperty('instance_name');

  //get the charactristic value - product instance id
  var prdInsId;
  var parentPrdInsID;
  var parentLineItem;
  /*
  var par = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
  par.addQuery('order_line_item' ,inputs.parentSys_id);
  par.addQuery('characteristic.name',"ProductInstanceId");
  par.query();
  if(par.next()){
    parentPrdInsID = par.characteristic_option_value.toString();
  }*/
 /* if(!(inputs.parentOrderLine == null)){
  parentLineItem = inputs.parentOrderLine;
  }*/
  var gr1 = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
  gr1.addQuery('order_line_item' ,inputs.orderLineID);
  gr1.addQuery('characteristic.name',"ProductInstanceId");
  gr1.query();
  if(gr1.next()){
    prdInsId = gr1.characteristic_option_value.toString();
  }


   // gs.eventQueue('LMNOrderUpdatesToKafka', null, JSON.stringify(jsonVal), 'DDC.DEV1.LUMEN-SERVICENOW.ENTERPRISE.ORDERORCHESTRATION.ORDER.SPECIFIC.EVENT');

   var jsonVal= {
			"messageId":"SN-"+gdt,
			"messageTime":gdt1.toISOString(),
			"messageType":inputs.messageType,
			"messageName":inputs.messageName,
			"pattern":"SPECIFIC.EVENT",
			"version":inputs.version,
			"source":"LUMEN-SERVICENOW",
			"correlationId":inputs.orderNum,
			"payload": {
				"callbackURL": "https://"+ instance_current +".service-now.com/api/sn_ind_tmt_orm/productorder/lumenEnriched/"+inputs.orderId+"?order_line_item_external_id="+ inputs.externalID,
				"productInstanceID":prdInsId,
              //	"parentProductInstanceID":parentPrdInsID,
				"orderID":inputs.orderNum,
              	"externalId":inputs.order_external_id,
              	"productOrderItemId":inputs.externalID,
             // 	"parentProductOrderItemId":parentLineItem,
				"billingAccountNumber": inputs.billingAccountNum
            }
   }
  outputs.prodorderobj =  JSON.stringify(jsonVal);
  outputs.responsearray = JSON.stringify(jsonVal);

})(inputs, outputs);

