sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...
  if (inputs.tskShortDescription == "MEDR Technical Data Gathering"){
    outputs.json_output = inputs.MEDRTechnicalDataGathering;
  }
  else if((inputs.tskShortDescription == "Cyber Threat Solutions Engineer Assignment")){   
    outputs.json_output = inputs.CyberThreatSolutionsEngineerAssignment;
  }
     }
})(inputs, outputs);

