sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

    var orderLineId = inputs.orderLineId;
    var updatedDate =  inputs.updatedDate;
  var gt = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); gt.addEncodedQuery("order_line_item="+orderLineId+"^characteristic=8b9ac36587d749d025a20edc0ebb35ab^ORcharacteristic=4245f2b787eb415025a20edc0ebb3580^ORcharacteristic=fd0a076d879749d025a20edc0ebb3551"); 
  gt.query();
  while(gt.next()){
    gt.characteristic_option_value = 
      (new Date(new GlideDateTime(updatedDate).getNumericValue()).toISOString());

    // updatedDate +" GMT";
    gt.update();
  }
})(inputs, outputs);

