sys_variable_value.value:
(function execute(inputs, outputs) {

var swiftpackage = inputs.swiftresponse.root_array;

var tmfpayload = {};
var productItems = [], relatedParty = [], party = {}, customers = [];

for (var p = 0; p < swiftpackage.length;  p++) {

var swiftinput = swiftpackage[p].products;

tmfpayload.id = swiftpackage[p].transactionId;
tmfpayload.ponr = false;
tmfpayload.requestedCompletionDate = swiftpackage[p].negotiatedDueDate;
tmfpayload.requestedStartDate = swiftpackage[p].committedDueDate;  
tmfpayload.orderType =  swiftpackage[p].action;    
tmfpayload.state =  swiftpackage[p].status;    
var channel = {}, channels = [];
channel.id = 1;
channel.name = swiftpackage[p].orderingSystem; 
channels.push(channel);  
tmfpayload.channel = channels;    

  for (var i = 0; i < swiftinput.length;  i++) {
    var productItem = {};  productOrderItemRelationships = [];
    productItem.id = swiftinput[i].productInstanceId;
    productItem.quantity = 1;
    productItem.action = "Add";
    var relatedParties = [], relatedParty = {}, product = {}, place = {}, productSpecification = {}, productOffering = {};
    product.isBundle = false;
	var attrList = swiftinput[i].productAttributes;
    var placeId = "";
      for (var l = 0; l < attrList.length; l++)  { 
        if (attrList[l].attributeName === "Address1") {
          placeId = attrList[l].attributeValue;
          break;
        }  
      }

	place.id = placeId;
  	place["@type"] = "Place";

	relatedParty.id = inputs.swiftresponse.customerId;
	relatedParty.name = inputs.customername;
	relatedParty.referredType = "OrderLineItemContact";
	relatedParties.push(relatedParty);
    product.relatedParty = relatedParties;
    product.place = place;
    productSpecification.version =  1;
    productSpecification.id =  swiftinput[i].pCatProductId;
    productSpecification.name =  swiftinput[i].productName;
    product.productSpecification = productSpecification;
    product.productCharacteristic = getProductCharacteristcs(swiftinput[i].productAttributes,swiftpackage[p].productPackageId, swiftpackage[p].businessOrderId);

    productItem.itemPrice = getProductPricing(swiftinput[i].priceInfo);

    productItem.product = product;

    productOffering.id = "21cae96e1b54785020339cef034bcb6a";
    productOffering.name = "Dedicated Internet Offer";
    productItem.productOffering = productOffering;

    if (swiftinput[i].isParentProduct === false || swiftinput[i].isParentProduct === "false") {
			var  cproductOrderItemRelationship = {};          
    		cproductOrderItemRelationship.id = swiftinput[i].parentProductInstanceId;
    		cproductOrderItemRelationship.relationshipType = "HasParent";
          	productOrderItemRelationships.push(cproductOrderItemRelationship);
    } else if (swiftinput[i].isParentProduct) {
      	var relatedIds = [];
        relatedIds = getChildren(swiftinput[i].productInstanceId , swiftinput);
        for (var j = 0; j < relatedIds.length;  j++) {
			var  productOrderItemRelationship = {};          
    		productOrderItemRelationship.id = relatedIds[j];
    		productOrderItemRelationship.relationshipType = "HasChild";
          	productOrderItemRelationships.push(productOrderItemRelationship);
        }
    }
    productItem.productOrderItemRelationship = productOrderItemRelationships;
    productItems.push(productItem)
  }
}
  tmfpayload.productOrderItem = productItems;  
  party.id = inputs.customerid;
  party.name = inputs.customername;
  party["@referredType"] = "Customer";
  customers.push(party);
  tmfpayload.relatedParty = customers; 

outputs.payload = JSON.stringify(tmfpayload);

function getProductCharacteristcs(list,productPackageId,businessOrderId) {
  var attrList = [];

  for (var k = 0; k < list.length; k++)  { 
    	var charObj = {};
    	charObj.name = list[k].attributeName;
    	charObj.valueType = "Single Line Text";
    	charObj.value = list[k].attributeValue;

      attrList.push(charObj);
  }

    	var pspAttribute = {};
    	pspAttribute.name = "GPID";
    	pspAttribute.valueType = "Single Line Text";
    	pspAttribute.value = productPackageId;
      attrList.push(pspAttribute);

    	var boidAttribute = {};
    	boidAttribute.name = "BusinessOrderId";
    	boidAttribute.valueType = "Single Line Text";
    	boidAttribute.value = businessOrderId;
      attrList.push(boidAttribute);

  return attrList;
} 

function getProductPricing(priceInfo) {
  var priceList = [];

    //if (priceInfo.mrcApplicable === true) {
  		var itemPrice = {}, price = {}, taxIncludedAmount = {};
  		itemPrice.priceType =  "recurring";
        taxIncludedAmount.unit = priceInfo.currencyCode;
        taxIncludedAmount.value = priceInfo.mrcAdjusted;
  		price.taxIncludedAmount =  taxIncludedAmount;
      	itemPrice.price = price;
      priceList.push(itemPrice);
    //} 

  //if (priceInfo.nrcApplicable === true) {
  		itemPrice = {}, price = {}, taxIncludedAmount = {};
  		itemPrice.priceType =  "nonRecurring";
        taxIncludedAmount.unit = priceInfo.currencyCode;
        taxIncludedAmount.value = priceInfo.nrcAdjusted;
  		price.taxIncludedAmount =  taxIncludedAmount;
      	itemPrice.price = price;
      priceList.push(itemPrice);
    //}   

  return priceList;
} 

function getChildren(parentId, list) {
  var children = [];

  for (var k = 0; k < list.length; k++)  {  
    if (parentId == list[k].parentProductInstanceId && parentId !== list[k].productInstanceId) {
      children.push(list[k].productInstanceId);
    }
  }

  return children;
} 


}
)
(inputs, outputs);

