sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var orderID = inputs.orderID;
  //Query in sase profile line item
  var gr = new GlideRecord('sn_ind_tmt_orm_order_line_item'); 
  gr.addEncodedQuery("order="+orderID+"^product_specification=9ed9cc5e8772819084b2a18e0ebb35ef"); 
  gr.query();
  if(gr.next()){
    //getting sase profile line item sys_id
    var SASE_PROFILE_sysID = gr.sys_id;

    //Query in namespace task to get namespace name
    var poTask = new GlideRecord('sn_ind_tmt_orm_order_task'); 
    poTask.addEncodedQuery("order_line_item="+SASE_PROFILE_sysID+"^short_descriptionLIKENamespace"); 
    poTask.query();
    if(poTask.next()){

      //Filtering short description to get namespace name
      var validatingStr = "SASE Profile - Create Namespace: ";
      var str = ""+poTask.short_description;
      var indxFoundStart = str.search(validatingStr); 
      var indxFoundEnd = validatingStr.length; 
      var textCompareLen = str.length; 

      //Passing namespaces name in var
      outputs.namespaces = str.substring(indxFoundEnd, textCompareLen);
    }
  }
})(inputs, outputs);

