sys_variable_value.value:
(function execute(inputs, outputs) {
  var producctOrder = new GlideRecord("sn_ind_tmt_orm_product_order");  
  producctOrder.addQuery("sys_id", inputs.currentProductOrderID);
  producctOrder.query();
  if(producctOrder.next()){
    var childProducctOrder = new GlideRecord("sn_ind_tmt_orm_product_order");  
    childProducctOrder.addQuery("parent", producctOrder.sys_id);
    childProducctOrder.query();
    childProducctOrder.next();
    if(childProducctOrder.getRowCount() == 0){
      producctOrder.u_child_po_s_closed = true;
      producctOrder.update();
    }
  }
})(inputs, outputs);

