sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var orderID = inputs.orderID;
  var productSpecification = inputs.productSpecification;
  var SASE_CPE_OrderLine_sysID = "";
  var SASE_PROFILE_OrderLine_sysID = "";
  var namespaces = "";



  //Query in sase profile line item
  var gr = new GlideRecord('sn_ind_tmt_orm_order_line_item'); 
  gr.addEncodedQuery("order="+orderID+"^product_specification="+gs.getProperty("productSpecification.SASE Profile")); 
  gr.query();
  if(gr.next()){
    //GETTING SASE PROFILE LINE ITEM DETAILS
    if(gr.product_specification == gs.getProperty("productSpecification.SASE Profile")){
      SASE_PROFILE_OrderLine_sysID = ""+gr.sys_id;
      //GETTING SASE NAMESPACE AND SASE RESOURCE
      var poTask = new GlideRecord('sn_ind_tmt_orm_order_task'); 
      poTask.addEncodedQuery("order_line_item="+SASE_PROFILE_OrderLine_sysID+"^short_descriptionLIKESASE Profile - Create Namespace:^ORshort_descriptionLIKESASE Profile - CreateSASEresource:"); 
      poTask.query();

      while(poTask.next()){
        //GETTING SASE NAMESPACE AND SASE RESOURCE
        if(poTask.short_description.includes("SASE Profile - Create Namespace:") == true){
          namespaces = poTask.short_description.replace("SASE Profile - Create Namespace: ",'')
        }
        if(poTask.short_description.includes("SASE Profile - CreateSASEresource: ") == true){
          outputs.saseresource = poTask.short_description.replace("SASE Profile - CreateSASEresource: ",'')
        }
      }
    }
  }

  //GETTING SASE CPE LINE ITEM DETAILS
  if(productSpecification == gs.getProperty("productSpecification.SASE CPE")){

    //Getting PIID and Edge Name from device char and sase vendor.
    var characteristics = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); 
    characteristics.addEncodedQuery("order_line_item="+inputs.currentOrderLine+"^characteristic="+gs.getProperty("Characteristic - ProductInstanceID(PIID)")+"^ORcharacteristic="+gs.getProperty("characteristic.deviceName")+"^ORcharacteristic="+gs.getProperty("Characteristic - SASE Vendor"));
    characteristics.query();
    while(characteristics.next()){
      if(characteristics.characteristic.name == "ProductInstanceId"){
        outputs.productinstanceid = ""+characteristics.characteristic_option_value;
      }
      if(characteristics.characteristic.name == "Device Name"){
        outputs.edgename = ""+characteristics.characteristic_option_value;
      }
      if(characteristics.characteristic.name == "SASE Vendor"){
        outputs.sase_vendor = ""+characteristics.characteristic_option_value;
      }
    }
  }

  //Getting SASE CPE Service Order ID
  var serviceOrderID = "";
  var serOrder = new GlideRecord("sn_ind_tmt_orm_service_order");
  serOrder.addEncodedQuery("order_line_item="+inputs.currentOrderLine+"^short_descriptionSTARTSWITHService Order for CPE")
  serOrder.query();
  if(serOrder.next()){
    serviceOrderID = ""+serOrder.sys_id;
    outputs.service_order_id = ""+serOrder.sys_id;
  }
  outputs.service_order_id = serviceOrderID;
  gs.info(">> AJ serviceOrderID "+serviceOrderID);

  //Checking Edge Task is Created or Not
  var chkEdgeCreated = "no";
  var tskExists = new GlideRecord("sn_ind_tmt_orm_order_task");
  tskExists.addEncodedQuery("order_line_item="+inputs.currentOrderLine+"^short_descriptionLIKEEdge Created^parent="+serviceOrderID)
  tskExists.query();
  if(tskExists.next()){
    chkEdgeCreated = "yes";
  }
  outputs.checked_edge_created = chkEdgeCreated;
  gs.info(">> AJ chkEdgeCreated "+chkEdgeCreated);


  //Checking Claim Task is Created or Not
  var chkClaimLicenseCreated = "no";
  var tskExists1 = new GlideRecord("sn_ind_tmt_orm_order_task");
  tskExists1.addEncodedQuery("order_line_item="+inputs.currentOrderLine+"^short_descriptionLIKESASE Claim License^parent="+serviceOrderID)
  tskExists1.query();
  if(tskExists1.next()){
    chkClaimLicenseCreated = "yes";
  }
  outputs.checked_claim_license_created = chkClaimLicenseCreated;
  gs.info(">> AJ chkClaimLicenseCreated on loc update "+chkClaimLicenseCreated);


  if(chkEdgeCreated == "yes" && chkClaimLicenseCreated == "no"){
    outputs.name_spaces = "apis/v1/namespaces/"+namespaces+"/licenseclaim";
    outputs.nm = namespaces;
  }

})(inputs, outputs);

