sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  //  var orderLineId = inputs.oli_sys_id;
  var orderLineId = inputs.orderLineId;
  var mrc=inputs.mrc;
   var gt = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); 
/*  gt.addQuery("order_line_item="+orderLineId);
  gt.addQuery("characteristic="3b4c56e987db051484b2a18e0ebb3538);*/
  gt.addEncodedQuery("order_line_item="+orderLineId+"^characteristic=3b4c56e987db051484b2a18e0ebb3538"); 

//  3b4c56e987db051484b2a18e0ebb3538

  gt.query();
  while(gt.next()){
    gt.characteristic_option_value = mrc;
    gt.update();
  }
})(inputs, outputs);

