sys_variable_value.value:
(function execute(inputs, outputs) {

  // ... code ...
  //Checking "Onsite Install Complete" Task is closed

  var productOrderID = "";
  var gr = new GlideRecord('sn_ind_tmt_orm_product_order'); 
  gr.addQuery("order_line_item", inputs.currentOrderLineID); 
  gr.query();
  if(gr.next()){
    productOrderID = ""+gr.sys_id;    
  }
  var tskExists = new GlideRecord("sn_ind_tmt_orm_order_task");  
  tskExists.addEncodedQuery("order_line_item.order_line_id="+inputs.charOrderLineNumber+"^short_description=Onsite Install Complete")
  tskExists.query();
  if(tskExists.next())
  {      
    if(tskExists.state == 5){
      var tskExists1 = new GlideRecord("sn_ind_tmt_orm_order_task");
      tskExists1.addEncodedQuery("order_line_item="+inputs.currentOrderLineID+"^short_description=Onsite Revenue Realize Task")
      tskExists1.query();
      tskExists1.next()
      if(tskExists1.getRowCount() == 0){
        var task = new GlideRecord('sn_ind_tmt_orm_order_task');
        task.initialize();
        task.order_line_item = inputs.currentOrderLineID;
        task.account = inputs.accountID;
        task.parent = productOrderID;
        task.short_description = "Onsite Revenue Realize Task";
        task.state = 5;    
        task.insert();
      }   
    }
  }
})(inputs, outputs);

