sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...
  var orderID = inputs.orderID;
  var orderLine = inputs.currentOrderLineID;
  var product_Specification = inputs.productSpecification;
  //Query in sase profile line item
  var gr = new GlideRecord('sn_ind_tmt_orm_order_line_item'); 
  gr.addEncodedQuery("order="+orderID+"^product_specification="+gs.getProperty("productSpecification.SASE Profile")); 
  gr.query();
  if(gr.next()){
    //GETTING SASE PROFILE LINE ITEM DETAILS
    if(gr.product_specification.name == "SASE Profile"){
      var SASE_PROFILE_sysID = gr.sys_id;
      //GETTING SASE NAMESPACE AND SASE RESOURCE
      var poTask = new GlideRecord('sn_ind_tmt_orm_order_task'); 
      poTask.addEncodedQuery("order_line_item="+SASE_PROFILE_sysID+"^short_descriptionLIKESASE Profile - Create Namespace:^ORshort_descriptionLIKESASE Profile - CreateSASEresource:"); 
      poTask.query();
      while(poTask.next()){
        //GETTING SASE NAMESPACE AND SASE RESOURCE
        if(poTask.short_description.includes("SASE Profile - Create Namespace:") == true){
          outputs.namespace = poTask.short_description.replace("SASE Profile - Create Namespace: ",'')
        }
        if(poTask.short_description.includes("SASE Profile - CreateSASEresource: ") == true){
          outputs.sase_resource = poTask.short_description.replace("SASE Profile - CreateSASEresource: ",'')
        }
      }
    }
  }
  //GETTING SASE CPE LINE ITEM DETAILS
  if(product_Specification == gs.getProperty("productSpecification.SASE CPE")){
    //Getting PIID and Edge Name from device char and sase vendor.
    var characteristics = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); 
    characteristics.addEncodedQuery("order_line_item="+orderLine+"^characteristic="+gs.getProperty("Characteristic - ProductInstanceID(PIID)")+"^ORcharacteristic="+gs.getProperty("characteristic.deviceName")+"^ORcharacteristic="+gs.getProperty("Characteristic - Edge Serial Number"));
    characteristics.query();
    while(characteristics.next()){
      if(characteristics.characteristic.name == "ProductInstanceId"){
        outputs.piid = ""+characteristics.characteristic_option_value;
      }
      if(characteristics.characteristic.name == "Device Name"){
        outputs.edge_name = ""+characteristics.characteristic_option_value;
      }
      if(characteristics.characteristic.name == "Edge Serial Number"){
        outputs.serial_number = ""+characteristics.characteristic_option_value;
      }
    }
  }
})(inputs, outputs);

