sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var orderLineID = inputs.orderLineID;
  var accountSysID = inputs.accountSysID;
  var soID = inputs.soID;

  var ResponseBody = JSON.parse(inputs.ResponseBody);

  gs.info("AJKS- HTTP STATUS "+inputs.HTTP_Status);
  //if(inputs.HTTP_Status == 200){
  var task = new GlideRecord('sn_ind_tmt_orm_order_task');
  task.initialize();
  task.order_line_item = orderLineID;
  task.account = accountSysID;
  task.parent = soID;
  task.short_description = "Obtain License Resource information task ";
  task.work_notes = "HTTP Status Code: "+inputs.HTTP_Status;
  task.work_notes = "Response Body: "+JSON.stringify(ResponseBody);
  if(inputs.HTTP_Status == 200){
    task.state = 5;
  }else{
    task.state = 7;
  } 
  task.insert();
  // }

  var characteristics = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); 
  characteristics.addEncodedQuery("order_line_item="+orderLineID+"^characteristic.nameSTARTSWITHVendor License Key^ORcharacteristic.nameSTARTSWITHVendor License Type^ORcharacteristic.nameSTARTSWITHVendor License Vendor Name");
  characteristics.query();
  while(characteristics.next()){
    if(characteristics.characteristic.name == "Vendor License Key"){
      characteristics.characteristic_option_value = ResponseBody.spec.vendorLicenseKey;
      characteristics.update();
    }
    if(characteristics.characteristic.name == "Vendor License Type"){
      characteristics.characteristic_option_value = ResponseBody.spec.vendorLicenseType;
      characteristics.update();
    }
    if(characteristics.characteristic.name == "Vendor License Vendor Name"){
      characteristics.characteristic_option_value = ResponseBody.spec.vendorLicenseVendorName;
      characteristics.update();
    }
  }
})(inputs, outputs);

