sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var orderLineId = inputs.orderLineId;
  var updatedDate =  inputs.updatedDate;
  var gt1 = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value'); gt1.addEncodedQuery("order_line_item="+orderLineId+"^characteristic=138ac36587d749d025a20edc0ebb35a8"); 
  gt1.query();
 if(gt1.next()){
   gt1.characteristic_option_value = (new Date(new GlideDateTime(updatedDate).getNumericValue()).toISOString());


     //updatedDate +" GMT";
   gt1.update();
  }

})(inputs, outputs);

