			"correlationId": gdt1,
			"payload": {
				"key": "LUMEN-SERVICENOW_" + inputs.TaskNumber,
				"workmatePayload": {
					"sourceTaskId": inputs.TaskNumber,
					"sourceSystemName": "LUMEN-SERVICENOW",
					"taskName": inputs.messagetype,
					"createdById": "LUMEN-SERVICENOW",
					"notes": "Test Notes",
					"commonInformation": {
						"accountNumber": inputs.accountNumber,
						"circuitId": "Test-1234",
						"customerId": inputs.customerId,
						"customerName": inputs.customerName,
						"dispatchArea": "Test",
						"orchestratorSystem": "LUMEN-SERVICENOW",
						"orderSource": "LUMEN-SERVICENOW",
						"orderVersion": inputs.version,
						"productName": inputs.productName,
						"serviceComponentId": inputs.parentID,
						"serviceId": inputs.parentID,
						"workLocationId": inputs.workLocationId,
						"orderId": inputs.orderNumber
					},
					"taskInstParamRequestList": [{
							"name": "serviceOrderNbr",
