var customerId= fd_data.trigger.service_order.account.u_customer_id;
var orderNumber = fd_data.trigger.service_order.number;
var customerContact =fd_data.trigger.service_order.order_line_item.contact.name;
var serviceWrapper ='Not Available';
var technicalDetails ='Not Available';
var lumenSKU ='Not Available';
var noOfEndpointsForMACOS ='Not Available';
var noOfEndpointsForLinuxOS ='Not Available';
var noOfEndpointsForWindowsOS ='Not Available';
var medrInstallation ='Not Available';
var addOnService ='Not Available';
var term ='Not Available';
var onetimeEvent='Not Available';
var orderTaskSysID ='';
