var antivirus ='';
var region='';
var dnsName='';
var os = '';
var os_version= '';
var securityProducts='Not Available';
var grOrderTask=new GlideRecord('sn_ind_tmt_orm_order_task');
grOrderTask.addEncodedQuery('parent.number='+fd_data.trigger.service_order.number+'^short_description=MEDR Task to Cyber Threat Solutions engineer assignment');
grOrderTask.query();
if(grOrderTask.next())
{
 orderTaskSysID = grOrderTask.sys_id;
 orderTaskNumber = grOrderTask.number;
}
var orderTaskURL="<a href='/sn_ind_tmt_orm_order_task.do?sys_id="+orderTaskSysID+"'>"+orderTaskNumber+"</a>"
var orderSysID = fd_data.trigger.service_order.sys_id;
var orderURL = "<a href='/sn_ind_tmt_orm_service_order.do?sys_id="+orderSysID+"'>"+orderNumber+"</a>"
var grCharacteristic = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
grCharacteristic.addQuery('order_line_item',fd_data.trigger.service_order.order_line_item);
//grCharacteristic.addQuery('specification','5f2ef44787e7015084b2a18e0ebb35c8');
grCharacteristic.query();
while(grCharacteristic.next()){
var characteristicName=grCharacteristic.getDisplayValue('characteristic');
var characteristicValue=grCharacteristic.getDisplayValue('characteristic_option_value');
if(characteristicValue!='')
{
if(characteristicName=="Customer Domain Name")
dnsName= characteristicValue;
if(characteristicName=="Other Security Products ")
securityProducts = characteristicValue;
if(characteristicName == 'Service Level')
serviceWrapper = characteristicValue;
if(characteristicName=='Region')
region=characteristicValue;
if(characteristicName=="Number of Linux EndPoints")
noOfEndpointsForLinuxOS = characteristicValue;
if(characteristicName=="Number of MS Windows EndPoints")
noOfEndpointsForWindowsOS = characteristicValue;
if(characteristicName=="Number of MacOS EndPoints")
noOfEndpointsForMACOS = characteristicValue;
if(characteristicName=="Term")
term = characteristicValue;
if(characteristicName=="One Time Event Type")
onetimeEvent = characteristicValue;
if(characteristicName=='AntiVirus included')
antivirus = characteristicValue;
}
}
var emailBody = '<p>Hi Team,<br /><br />A new MEDR order has been submitted per SLO’s. Please reach out to the following customer within 1 week to set up Kick Off call&nbsp; :</p><table style="border: none;"><tbody><tr><td>Task ID :</td><td>'+orderTaskURL+'</td></tr><tr><td>Customer Name :</td><td>'+customerName+'</td></tr><tr><td>Customer ID:</td><td>'+customerId+'</td></tr><tr><td>Service Order</td><td>'+orderURL+'</td></tr><tr><td>Order Number:</td><td>'+orderNum+'</td></tr><tr><td>Customer Domain Name:</td><td>'+dnsName+'</td></tr><tr><td>Other Security Products:</td><td>'+securityProducts+'</td></tr><tr><td>Service Level :</td><td>'+serviceWrapper+'</td></tr><tr><td>Number of Linux EndPoints :</td><td>'+noOfEndpointsForLinuxOS+'</td></tr><tr><td>Number of MS Windows EndPoints :</td><td>'+noOfEndpointsForWindowsOS+'</td></tr><tr><td>Number of MacOS EndPoints :</td><td>'+noOfEndpointsForMACOS+'</td></tr><tr><td>Antivirus :</td><td>'+antivirus+'</td></tr><tr><td>Customer Contact :&nbsp;</td><td>'+customerContact+'</td></tr><tr><td>One Time event type :</td><td>'+onetimeEvent+'</td></tr><tr><td>Term :</td><td>'+term+'&nbsp;</td></tr></tbody></table><p><br />Thanks,<br />ServiceNow</p><p>&nbsp;</p>';
