sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
 var createUserInLIAM = new LIAMAPI().getUserLIAM(current.order_line_item.order.contact, current.order_line_item.order.contact.email) + '';
    gs.info("**********LIAM createUserInLIAM**********" + createUserInLIAM);
    if (createUserInLIAM == 'User has MEDR Security Role') {
        var externalID = current.order_line_item.order.external_id;
        var orderContact = current.order_line_item.order.contact;
        var orderLineNum = current.order_line_item;
        //gs.info("****SOAR Order number*****" + orderContact + "\n SOAR ORDER LIne Num" + orderLineNum + "******ExternalID****" + externalID);
        var createUser = new SOARAPI().createUserInSOAR(current, externalID, orderContact, orderLineNum) + '';
        gs.info("******SOAR User Response : " + createUser);
        if (createUser == 'User is created successfully') {
			gs.eventQueue('sn_ind_tmt_orm.soar.sucessful', current, current.assignment_group, '', '');
            gs.setRedirect('/sn_ind_tmt_orm_order_task.do?sys_id='+current.sys_id);
			current.work_notes='All the configurations have been configured successfully.';
            current.state = 5;
            current.update();
        } else {
			gs.eventQueue('sn_ind_tmt_orm.soar.unsucessful', current, 'dl-soar@lumen.com', '', '');
            gs.setRedirect('/sn_ind_tmt_orm_order_task.do?sys_id='+current.sys_id);
			current.work_notes="There is some technical error occurred at SOAR end, Team has been notified for the fault , please allow some time to get it fix.";
            current.update();
        }

    }
	else
		{
			current.work_notes=createUserInLIAM;
            current.update();
		}

})(current, previous);
