sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    gs.info('Retrieving email from Salesforce', 'ac');
    var digesttime = new Date().getTime();
    var digest = 'AK110965420170';

    //Getting APIGEE Token
    var APIGEEToken = new SOARAPI().getAPIGEEToken(current.order_line_item.order.contact, current.order_line_item.order.contact.email);
    var legalAddress = '';
    gs.info('got APIGEE Token' + APIGEEToken, 'ac');

    //Passing Salesforce endpoint
    var r = new sn_ws.RESTMessageV2('SalesforceEndpoint', 'POSTDetails');

    //Passing token to Endpoint
    r.setRequestHeader('x-level3-application-key', AIPGEEToken);
    r.setRequestHeader('x-level3-digest', digest);
    r.setRequestHeader('x-level3-digest-time', digesttime);
    var response = r.execute();
    var responseBody = response.getBody();
    var httpStatus = response.getStatusCode();
    gs.info('Response' + httpStatus);
    if (httpStatus == '200' || httpStatus == '201') {
        returnResponse = 'Email retrieved successfully';
    } else {
        returnResponse = 'Email not retrieved due to error: ' + httpStatus;
    }

})(current, previous);
