sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
 var grCharOpt = current.characteristic_option.getDisplayValue();
    gs.info('charct value smya' + grCharOpt);

    var grCustTask = new GlideRecord('sn_ind_tmt_orm_order_task');
    grCustTask.addQuery('order_line_item', current.order_line_item);

    grCustTask.query();
    if (grCustTask.next()) {
        var grCreatedDT = grCustTask.sys_created_on;
        gs.info('createdon date smya' + grCreatedDT);

        var gdt = new GlideDateTime(grCreatedDT);
        gdt.addMonthsLocalTime(grCharOpt);
        gs.info('months added1 smya' + gdt.addMonthsLocalTime(grCharOpt));

        var grCustorder = new GlideRecord('sn_ind_tmt_orm_order');
        grCustorder.addQuery('sys_id', current.order_line_item.order);

        grCustorder.query();
        if (grCustorder.next()) {
            gs.info('into 2nd if smya');
            grCustorder.expected_end_date = gdt.getLocalDate();
            gs.info('end date is smya' + grCustorder.expected_end_date);
			 grCustorder.update();
        }


    }





})(current, previous);
