sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var other_security_prod = '';
    var other_security_prod_msg = '';
    var gr = new GlideRecord("sn_ind_tmt_orm_order_characteristic_value");
    gr.addEncodedQuery("order_line_item=" + current.order_line_item + "^characteristic.name=Customer Domain Name^ORcharacteristic.name=Number of Endpoints Linux OS^ORcharacteristic.name=Number of Endpoints MAC OS^ORcharacteristic.name=Number of Endpoints MS Windows OS^ORcharacteristic.name=Region with AP^ORcharacteristic.name=Region with EU^ORcharacteristic.name=Region with NA^ORcharacteristic.name=Other Security Products ");
    gr.query();
    var msg = "Please populate the TDG details by clicking the TDG Details button. '<b>TDG Details</b>' . The following Characteristics are missing values :";
    var flag = false;
    while (gr.next()) {
        var characteristicName = gr.characteristic.name + '';
        var characteristicValue = gr.characteristic_option_value + '';
        if (characteristicValue == '') {
            msg += characteristicName + ',';
            flag = true;
        }
        if (characteristicName == 'Customer Domain Name' && characteristicValue != '') {
            other_security_prod = characteristicValue.indexOf('.com');
            if (other_security_prod < 0) {
                other_security_prod_msg = 'Incorrect value in the Customer Domain Name characteristic. Please include .com in it';
            }
        }

    }
    if (flag == true) {
        current.setAbortAction(true);
        gs.addErrorMessage(msg);
    }
    if (other_security_prod_msg != '')
		{
        gs.addErrorMessage(other_security_prod_msg);
		current.setAbortAction(true);
		}
})(current, previous);
