sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var orderLineStartDate = [];

	var term = current.characteristic_option.getDisplayValue();

	gs.info("Checking Term: "+term);
	gs.info("Checking order_line_item: "+current.order_line_item);

	var startDate = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
	startDate.addEncodedQuery("order_line_item="+current.order_line_item+"^characteristic=ab3bcf6587d749d025a20edc0ebb356b");
	startDate.query();
	if(startDate.next()) {
		orderLineStartDate.push('' + startDate.characteristic_option_value); //getting start date
	} 
	gs.info("Checking orderLineStartDate: "+orderLineStartDate);

//order_line_item=16fd480e873b419025a20edc0ebb3505^characteristic=cf5d05d4873e095084b2a18e0ebb35db^characteristic_option!=NULL
	//Updating end date in chars.
	var gdt = new GlideDateTime(orderLineStartDate);
	var ed = new GlideDateTime(gdt);
	ed.addMonthsUTC(term);
	var abc = ed;
	gs.info("Checking abc: "+abc);
	var updateEndDate = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
updateEndDate.addEncodedQuery("order_line_item="+current.order_line_item+"^characteristic=9b2b0ba587d749d025a20edc0ebb355f");
	updateEndDate.query();
	if (updateEndDate.next()) {
		updateEndDate.characteristic_option_value = abc; //gDate;// gDate; // + " 00:00:00";
		updateEndDate.update();
	}

})(current, previous);
