sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var request, response, status, quoteId="NULL", subBandwidth , bandwidth , handOff, encapculation, gpid, serviceAddres1;

    var mid = getMidServer();

	designId = '2808919';
	request = new sn_ws.RESTMessageV2();
	request.setEndpoint('http://ise/Application/v1/Set/IseExternal/design/ise-' + designId);
	request.setHttpMethod('GET');
    request.setMIDServer(mid);
	request.setRequestHeader("Accept","application/json");
	request.setRequestHeader('Content-Type','application/json');
	response = request.execute();
	gs.info('Response body from iSE API ' + response.getBody());
	var iseOrder = JSON.parse(response.getBody());
	quoteId = iseOrder.quoteIds;

	bandwidth = iseOrder.sites[0].siteProducts[0].attributes.portspeed;
	subBandwidth = iseOrder.sites[0].siteProducts[0].attributes.cdr;
	handOff = iseOrder.sites[0].siteProducts[0].attributes.customerfibermode ;
	gpid = iseOrder.sites[0].siteProducts[0].attributes.serviceid;
	serviceAddres1 = iseOrder.sites[0].clliCode;
	if (bandwidth == "GigE") {
		encapsulation = "Ethernet";
	}

	var chars = new GlideRecord("sn_ind_tmt_orm_order_characteristic_value");
	chars.addEncodedQuery("domain_order=" + current.sys_id);
	chars.query();
	while(chars.next()) {
		if(chars.characteristic.name == "PCATProductID") {
			chars.characteristic_option_value = chars.order_line_item.product_specification_code;
		}

		if(chars.characteristic.name == "Network Status") {
			chars.characteristic_option_value = "On Net";
		}

		if(chars.characteristic.name == "ServiceAddress2") {
			chars.characteristic_option_value = current.parent.number;
		}

		if(chars.characteristic.name == "productComponentCode") {
			chars.characteristic_option_value = "191";
		}

		if(chars.characteristic.name == "GPID") {
			chars.characteristic_option_value = gpid;
		}

		if(chars.characteristic.name == "Service Address 1") {
			chars.characteristic_option_value = serviceAddres1;
		}

		if(chars.characteristic.name == "Access Sub Bandwidth") {
			chars.characteristic_option_value = subBandwidth;
		}

		if(chars.characteristic.name == "Circuit Type") {
			chars.characteristic_option_value = bandwidth;
		}

		if(chars.characteristic.name == "accessQuantity") {
			chars.characteristic_option_value = "1";
		}

		if(chars.characteristic.name == "Customer Handoff") {
			chars.characteristic_option_value = handOff;
		}

		if(chars.characteristic.name == "Encapsulation") {
			chars.characteristic_option_value = encapsulation;
		}

		chars.update();

	}

	function getMidServer() {
      var gr = new GlideRecord('ecc_agent');
      gr.addEncodedQuery('statusSTARTSWITHup^validatedINtrue');
      gr.setLimit(1);
      gr.query();
      if (gr.next()) {
              return gr.name;
      } else {
              return null;
      }
	}

})(current, previous);
