sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var chars = new GlideRecord("sn_ind_tmt_orm_order_characteristic_value");
	chars.addEncodedQuery("domain_order=" + current.sys_id);
	chars.query();
	while(chars.next()) {
		if(chars.characteristic.name == "pcatproductid") {
			chars.characteristic_option_value = chars.order_line_item.product_specification_code;
		}

		if(chars.characteristic.name == "Phone Number") {
			chars.characteristic_option_value = current.contact.mobile_phone;
		}

		if(chars.characteristic.name == "User") {
			chars.characteristic_option_value = current.contact.name;
		}

		if(chars.characteristic.name == "Customer Number") {
			chars.characteristic_option_value = chars.order_line_item.account.name;
		}
	}

})(current, previous);
