sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here




    var grSITOOLI = new GlideRecord('sn_ind_tmt_orm_order_line_item');
    grSITOOLI.addQuery('order', current.sys_id);
    grSITOOLI.query();
    while (grSITOOLI.next()) {
        var url = '<a title = "' + grSITOOLI.getDisplayValue() + '" href = "/sn_ind_tmt_orm_order_line_item.do?sys_id=' + grSITOOLI.getUniqueValue() + '">' + grSITOOLI.getDisplayValue() + '</a>';
        template.print(url +'<br/>');
    }


})(current, template, email, email_action, event);
