sys_script_include.script:
var TDGShowHideFields = Class.create();
TDGShowHideFields.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getCharName: function() {
        //var props = gs.getProperty('sn_ind_tmt_orm.DIATDGcondition');
        var props = gs.getProperty('sn_ind_tmt_orm.TDGTestDIA');
        return props;

    },

    getCharacteristicsFromSO: function() {
        var serviceOrder = this.getParameter("sysparm_serviceOrder");

        var grOpt = new GlideRecord('sn_ind_tmt_orm_order_characteristic_value');
        grOpt.addQuery("domain_order", serviceOrder);
        grOpt.query();
        var characteristicsList = [];
        while (grOpt.next()) {
            // Fetch input type from  sn_prd_pm_characteristic table
            var grc = new GlideRecord('sn_prd_pm_characteristic');
            grc.addQuery("sys_id", grOpt.characteristic);
            grc.query();
            var reqs = [];
            if (grc.next()) {
                if (grc.input_type == "choice" || grc.input_type == "yes_no") {
                    // Fetch metadata for dropdown values. No records if it is not a multiple choice. 
                    var grOptn = new GlideRecord('sn_prd_pm_characteristic_option');
                    grOptn.addQuery("characteristic", grc.sys_id);
                    grOptn.query();
                    var optnlist = '';

                    while (grOptn.next()) {
                        optnlist = grOptn.option.toString();
                        reqs.push(optnlist);
                    }
                }
                characteristicsList.push({
                    'name': grc.name.toString(),
                    'type': grc.input_type.toString(),
                    'sys_id': grc.sys_id.toString(),
                    'optvalue': grOpt.characteristic_option_value.toString(),
                    'optnlist': reqs
                });
            }
        }
        return JSON.stringify(characteristicsList);

    },

    updateCharValues: function() {
        var serviceOrder = this.getParameter("sysparm_serviceOrder");
        var chars = this.getParameter("sysparm_chars");

        charJson = JSON.parse(chars);

        for (var i = 0; i < charJson.length; i++) {
            var gr = new GlideRecord("sn_ind_tmt_orm_order_characteristic_value");
            gr.addQuery('domain_order', serviceOrder);
            gr.addQuery('characteristic', charJson[i].id);
            gr.query();

            while (gr.next()) {
                if (gr.characteristic.input_type == "choice" || gr.characteristic.input_type == "yes_no") {
                    gr.characteristic_option.setDisplayValue(charJson[i].val);
                }

                gr.characteristic_option_value = charJson[i].val;
                gr.update();
            }
        }

        return true;
    },

    type: 'TDGShowHideFields'
});
