var SalesforceAPI = Class.create();
SalesforceAPI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
     getAPIGEEToken: function() {
        try {
            var APIGEEToken = '';
            var r = new sn_ws.RESTMessageV2('GetAPIGEEToken', 'GetTokenSalesforce');
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            var SOARToken = '';
			gs.info('****script include outside of IF block****');
            if (httpStatus == '200' || httpStatus == '201') {
                var parseResponse = JSON.parse(responseBody);
                APIGEEToken = parseResponse.access_token;
                gs.info("****APIGEE token******" + APIGEEToken);
            }
        } catch (ex) {
            var message = ex.message;
			gs.error('****error message' + message);
        }
        return APIGEEToken;
    },
    type: 'SalesforceAPI'
});
