sys_ui_action.client_script_v2:
function onClick(g_form) {
    var msg = getMessage("Cancel this order line item?");
    g_modal.confirm(getMessage("Confirmation"), msg, function(confirmed) {
        if (confirmed) {
            g_form.submit("cancel_order_line_item");
        }
    });
}

sys_ui_action.script:
function cancelOrderLineItemConfirmation() {
    var dialog = new GlideModal('glide_modal_confirm', true, 400);
    dialog.setTitle(new GwtMessage().getMessage('Confirmation'));
    dialog.setWidth(275);
    dialog.setPreference('body', new GwtMessage().format("Cancel this order line item?"));
    dialog.setPreference('focusTrap', true);
    dialog.setPreference('onPromptComplete', doComplete);
    dialog.render();

    function doComplete() {
        gsftSubmit(null, g_form.getFormElement(), 'cancel_order_line_item');
    }
    return false;
}

if (typeof window == 'undefined')
    cancelOrderLineItem();

function cancelOrderLineItem() {
    current.revision_operation = sn_ind_tmt_orm.Constants.REVISION_OPERATION_CANCEL;
    current.state = sn_ind_tmt_orm.Constants.ORDER_STATE_ASSESSING_CANCELLATION;
    current.update();
    action.setRedirectURL(current);
}
