sys_ui_page.client_script:
var elems = document.getElementsByName("ord_characteristics");
for (var i = 0; i < elems.length; i++) {
    showField(elems[i].parentElement.getAttribute("name"), elems[i].id);
}

function showField(field, id) {
    console.log("Calling func - " + field + ":" + id);
    ga = new GlideAjax('sn_ind_tmt_orm.TDGShowHideFields');
    ga.addParam('sysparm_name', "getCharName");
    ga.getXML(getFieldsCallback);

    function getFieldsCallback(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        console.log(answer);
        var attributes = JSON.parse(answer.toString());
        //	g_form.addInfoMessage(attributes);
        console.log(attributes);
        var LableList = [];
        attributes.forEach(function(list) {
            console.log(field + "::" + list.Label + "::" + list.routingProtocol + "::" + (list.Label == field));
            if (list.Label == field) {
                console.log("found" + "::" + (list.routingProtocol == "true" ? "block" : "none"));
                if (list.routingProtocol == "false") {
                    document.getElementById(id).parentElement.parentElement.style.display = "none";
                }
            }
        });
    }
}

function continueOK() {
    //alert("OK clicked");
    var gdw = GlideDialogWindow.get();
    var orderLine = gdw.getPreference('orderLine');
    //	var chrname = gel('chr_name').value;
    var chrs = document.getElementsByName('ord_characteristics');
    //	var Ichr = gel('iterated_char').value;
    var val = '';
    for (var i = 0; i < chrs.length; i++) {
        val += chrs[i].value;

        //	alert(val+"-----");

        var gr = new GlideRecord("sn_ind_tmt_orm_order_characteristic_value");
        gr.addQuery('order_line_item', orderLine);
        gr.addQuery('characteristic', chrs[i].id);
        gr.query();
        console.log(gr.getEncodedQuery());
        while (gr.next()) {
            gr.characteristic_option_value = chrs[i].value;
            gr.update();
        }

    }

    GlideDialogWindow.get().destroy();
}



function continueCancel() {
    //alert("Cancelled");
    GlideDialogWindow.get().destroy();
}
