sys_ui_page.client_script:
var elems = document.getElementsByName("ord_characteristics");
if(elems != null) {
	for (var i = 0; i < elems.length; i++) {
		showField(elems[i].parentElement.getAttribute("name"), elems[i].id);
	}
}

var serviceOrder;
function populateChars(serviceOrderVar) {
	serviceOrder = serviceOrderVar;
    var serviceOrdersList = document.getElementsByName("serviceOrders");

    for (var i = 0; i < serviceOrdersList.length; i++) {
        serviceOrdersList[i].classList.remove("ui-tabs-active");
        serviceOrdersList[i].classList.remove("ui-state-active");
		serviceOrdersList[i].classList.add("ui-state-default");
    }

	serviceOrder.classList.remove("ui-state-default");
    serviceOrder.classList.add("ui-tabs-active");
    serviceOrder.classList.add("ui-state-active");

    if (document.getElementById("optnTable")) {
        document.getElementById("optnTable").remove();
    }

    var getCharAjax = new GlideAjax('sn_ind_tmt_orm.TDGShowHideFields');
    getCharAjax.addParam("sysparm_name", "getCharacteristicsFromSO");
    getCharAjax.addParam("sysparm_serviceOrder", serviceOrder.id);
    getCharAjax.getXMLAnswer(populateCharTable);
}

function populateCharTable(answer) {

    var characteristicsList = JSON.parse(answer);
	var tableHtml = '<table width="100%" id="optnTable" class="ui-tabs-panel ui-corner-bottom ui-widget-content" aria-hidden="false">';

	for (var i = 0; i < characteristicsList.length; i += 2) {

        tableHtml += '<tr>' +
            '<td style="width:"20%" class="did-floating-label-content">';
        if (characteristicsList[i].type == 'single_line_text') {
          //  tableHtml += '<td style="width:30%" name="' + characteristicsList[i].name + '">';
            tableHtml += '<input class="did-floating-input" type="text" name="ord_characteristics" id="' + characteristicsList[i].sys_id + '" value="' + characteristicsList[i].optvalue + '" />' +
				'<label class="did-floating-label">' + characteristicsList[i].name + '</label>' +
                '</td>';
		}

        if (characteristicsList[i].type == 'choice' || characteristicsList[i].type == 'yes_no') {
           // tableHtml += '<td class="did-floating-label-content" style="width:30%" name="' + characteristicsList[i].name + '">' +
                tableHtml += '<select class="did-floating-select" name="ord_characteristics" id="' + characteristicsList[i].sys_id + '">' +
                '<option value = ""> ---Select--- </option>';
            for (var j = 0; j < characteristicsList[i].optnlist.length; j++) {
				if(characteristicsList[i].optvalue == characteristicsList[i].optnlist[j]) {
					tableHtml += '<option selected value="' + characteristicsList[i].optnlist[j] + '">' + characteristicsList[i].optnlist[j] + '</option>';
				}
				else {
                tableHtml += '<option value="' + characteristicsList[i].optnlist[j] + '">' + characteristicsList[i].optnlist[j] + '</option>';
				}
            }
            tableHtml += '</select>';
            tableHtml += '<label class="did-floating-label">' + characteristicsList[i].name + '</label>' +
                '</td>';

        }

		tableHtml += '<td style="width:"20%" class="did-floating-label-content">';
            //'<label class="did-floating-label">' + characteristicsList[i + 1].name + '</label></td>';
        if (characteristicsList[i + 1].type == 'single_line_text') {
           // tableHtml += '<td style="width:30%" name="' + characteristicsList[i + 1].name + '">';
            tableHtml += '<input class="did-floating-input" type="text" name="ord_characteristics" id="' + characteristicsList[i + 1].sys_id + '" value="' + characteristicsList[i + 1].optvalue + '" />' +
				'<label class="did-floating-label">' + characteristicsList[i + 1].name + '</label>' +
                '</td>';
		}

        if (characteristicsList[i + 1].type == 'choice' || characteristicsList[i + 1].type == 'yes_no') {

                tableHtml += '<select class="did-floating-select" name="ord_characteristics" id="' + characteristicsList[i + 1].sys_id + '">' +
                '<option value = ""> ---Select--- </option>';
            for (var j = 0; j < characteristicsList[i + 1].optnlist.length; j++) {
				if(characteristicsList[i].optvalue == characteristicsList[i].optnlist[j]) {
					tableHtml += '<option selected value="' + characteristicsList[i].optnlist[j] + '">' + characteristicsList[i].optnlist[j] + '</option>';
				}
				else {
                tableHtml += '<option value="' + characteristicsList[i + 1].optnlist[j] + '">' + characteristicsList[i + 1].optnlist[j] + '</option>';
				}
            }
            tableHtml += '</select>';
			tableHtml += '<label class="did-floating-label">' + characteristicsList[i + 1].name + '</label>' +
            '</td>';

        }
     '</tr>';
    }
    tableHtml += "</table>";

    document.getElementById("optnDiv").insertAdjacentHTML("afterend",
        tableHtml);
}

function showField(field, id) {
    ga = new GlideAjax('sn_ind_tmt_orm.TDGShowHideFields');
    ga.addParam('sysparm_name', "getCharName");
    ga.getXML(getFieldsCallback);

    function getFieldsCallback(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        var attributes = JSON.parse(answer.toString());
        //	g_form.addInfoMessage(attributes);
        var LableList = [];
        attributes.forEach(function(list) {

            if (list.Label == field) {
                if (list.routingProtocol == "false") {
                    document.getElementById(id).parentElement.parentElement.style.display = "none";
                }
            }
        });
    }
}

function continueOK() {
    //alert("OK clicked");
    var gdw = GlideDialogWindow.get();
    var serviceOrderId = serviceOrder.id;
    //	var chrname = gel('chr_name').value;
    var chrs = document.getElementsByName('ord_characteristics');
    //	var Ichr = gel('iterated_char').value;
    var chars = [];
    for (var i = 0; i < chrs.length; i++) {
        var charElem = {};
        charElem.id = chrs[i].id;
        charElem.val = chrs[i].value;
        chars.push(charElem);
    }

    var updateValsGa = new GlideAjax("sn_ind_tmt_orm.TDGShowHideFields");
    updateValsGa.addParam("sysparm_name", "updateCharValues");
    updateValsGa.addParam("sysparm_chars", JSON.stringify(chars));
    updateValsGa.addParam("sysparm_serviceOrder", serviceOrderId);

    updateValsGa.getXMLAnswer(valuesUpdated);

    function valuesUpdated(answer) {
		if (answer == "true")
            GlideDialogWindow.get().destroy();
    }
}

function continueCancel() {
    //alert("Cancelled");
    GlideDialogWindow.get().destroy();
}
