sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var tsmOpenAPIUtil = new sn_ind_tsm_core.TSMOpenAPIUtil();
    response.setContentType(sn_ind_tsm_core.Constants.CONTENT_TYPE);
    try {
        var tmfProductInventoryGetAPIUtil = new TMF622GetOrderDetailsAPIUtil();
        var apiResponse = tmfProductInventoryGetAPIUtil.processListProductOrder(request.queryParams, request.url.toString());
        response.setStatus(apiResponse.status);
        //Pagination Status
        if (!gs.nil(apiResponse.totalCount)) {
            response.setHeader('X-Total-Count', apiResponse.totalCount);
            response.setHeader('Content-Range', apiResponse.contentRange);
            response.setHeader('Link', apiResponse.links);
        }
        return tsmOpenAPIUtil.responseBuilder(response, apiResponse.details);
    } catch (ex) {
        response.setStatus(sn_ind_tsm_core.Constants.STATUS_CODES.ERROR);
        var apiCoreUtil = new sn_ind_tsm_core.APICoreUtil();
        var errorObj = apiCoreUtil.getErrorObj(sn_ind_tsm_core.Constants.ERROR_CODES.INTERNAL_ERROR, sn_ind_tsm_core.Constants.MESSAGES.GET_ERROR, sn_ind_tsm_core.Constants.MESSAGES.GET_ERROR);
        var details = [];
        details.push(apiCoreUtil.getErrorDetailsObj(ex.message + '', ''));
        errorObj.details = details;
        return tsmOpenAPIUtil.responseBuilder(response, errorObj);
    }

})(request, response);
