sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var tsmOpenAPIUtil = new sn_ind_tsm_core.TSMOpenAPIUtil();
    response.setContentType(TMFOrderAPIConstants.CONTENT_TYPE);
    try {

        var tmfProductOrderGetAPIUtil = new TMFProductOrderAPIUtil1();
        var apiResponse1 = tmfProductOrderGetAPIUtil.processPatchOrder(request.pathParams.id, request.body.data);
        response.setStatus(apiResponse1.status);
        return tsmOpenAPIUtil.responseBuilder(response, apiResponse1.details);
    } catch (ex) {
        response.setStatus(sn_ind_tsm_core.Constants.STATUS_CODES.ERROR);
        var apiCoreUtil = new sn_ind_tsm_core.APICoreUtil();
        var errorObj = apiCoreUtil.getErrorObj(sn_ind_tsm_core.Constants.ERROR_CODES.INTERNAL_ERROR, TMFOrderAPIConstants.MESSAGES.PATCH_ERROR, TMFOrderAPIConstants.MESSAGES.PATCH_ERROR);
        var details = [];
        details.push(apiCoreUtil.getErrorDetailsObj(ex.message + '', ''));
        errorObj.details = details;
        return tsmOpenAPIUtil.responseBuilder(response, errorObj);
    }

})(request, response);
