sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var tsmOpenAPIUtil = new sn_ind_tsm_core.TSMOpenAPIUtil();
    response.setContentType(sn_ind_tsm_core.Constants.CONTENT_TYPE);
    try {
		var queryparams = request.queryParams;  
		var myQueryParam = queryparams.order_line_item_external_id;
		gs.info("Checking Query Parameter Value - "+myQueryParam);

        var tmfProductOrderGetAPIUtil = new TMF622GetOrderDetailsAPIUtil();
        var apiResponse1 = tmfProductOrderGetAPIUtil.processGetOrder(request.pathParams.id, myQueryParam, request.queryParams.myQueryParam);
        response.setStatus(apiResponse1.status);
        return tsmOpenAPIUtil.responseBuilder(response, apiResponse1.details);
    } catch (ex) {
        response.setStatus(sn_ind_tsm_core.Constants.STATUS_CODES.ERROR);
        var apiCoreUtil = new sn_ind_tsm_core.APICoreUtil();
        var errorObj = new apiCoreUtil.getErrorObj(sn_ind_tsm_core.Constants.ERROR_CODES.INTERNAL_ERROR, sn_ind_tsm_core.Constants.MESSAGES.GET_ERROR, sn_ind_tsm_core.Constants.MESSAGES.GET_ERROR);
        var details = [];
        details.push(apiCoreUtil.getErrorDetailsObj(ex.message + '', ''));
        errorObj.details = details;
        return tsmOpenAPIUtil.responseBuilder(response, errorObj);
    }
})(request, response);
